-- Records of category
-- ----------------------------
INSERT INTO `category` VALUES (1, '前端', NULL, NULL, 0);
INSERT INTO `category` VALUES (2, '后端', NULL, NULL, 0);

-- ----------------------------
-- Table structure for comment
-- ----------------------------
DROP TABLE IF EXISTS `comment`;
CREATE TABLE `comment`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '记录值',
  `article_id` int(10) UNSIGNED NOT NULL COMMENT '文章ID',
  `parent_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '回复ID',
  `context` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '内容',
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '匿名' COMMENT '联系方式 姓名',
  `email` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '联系方式 邮箱',
  `website` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '联系方式 地址',
  `ip` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '回复时IP',
  `city` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '城市',
  `create_time` int(11) UNSIGNED NOT NULL COMMENT '创建时间',
